import React, { useEffect, useState } from "react";
import Chat from "./Chat";
import Modal from "./Modal";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Table = ({ isGroupAdded }) => {
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const [groups, setGroups] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:9000/api/groups").then((res) => {
      setGroups(res.data.groups);
      console.log(res.data.groups)
    });
  }, [isGroupAdded]);


  return (
    <div>
      <Modal open={open} setOpen={setOpen} />
      <div class="relative overflow-x-auto shadow-md sm:rounded-lg mt-10">
        <table class="w-full text-sm text-left text-gray-500 dark:text-gray-400 bg-white">
          <thead class="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
            <tr>
              <th scope="col" class="px-6 py-3">
                Group Name
              </th>
              <th scope="col" class="px-6 py-3">
                Total Phone No.
              </th>
              <th scope="col" class="px-6 py-3">
                Total Answered
              </th>
              <th scope="col" class="px-6 py-3">
                Total Answered
              </th>
              <th scope="col" class="px-6 py-3">
                Created Date/Time
              </th>
              <th scope="col" class="px-6 py-3 cursor-pointer">
                Edit
              </th>
            </tr>
          </thead>
          <tbody>
            {groups?.map((item) => (
              <tr class="bg-white border-b dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600">
                <th
                  scope="row"
                  class="flex items-center px-6 py-4 text-gray-900 whitespace-nowrap dark:text-white"
                >
                  <img
                    class="w-10 h-10 rounded-full"
                    src="/imgs/gallery.svg"
                    alt="Jese image"
                  />
                  <div class="pl-3">
                    <div class="text-base font-semibold">{item?.name}</div>
                  </div>
                </th>
                <td class="px-6 py-4 text-center">{item?.totalPhoneNumbers}/10283</td>
                <td class="px-6 py-4 text-center">{item?.totalAnswered}</td>
                <td class="px-6 py-4 text-center">{item?.totalDuration}</td>
                <td class="px-6 py-4 text-center">12-12-2023</td>
                <td
                  class="px-6 py-4 text-center cursor-pointer"
                  onClick={() => navigate(`/group-details/${item?._id}`)}
                >
                  <img src="/imgs/edit.svg" />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Table;
