import React from 'react';
import Button from './Button';

const DeleteModal = ({ show, onClose, onConfirm, doctor }) => {
  if (!show) return null;
    console.log('opennnnnnnnn', show)
  return (
    <div className="fixed inset-0 flex items-center justify-center z-10">
        <div className="modal bg-white p-6 rounded w-5/12 shadow-2xl" style={{ margin: '0 auto' }}>
      <div className="modal-content">
        <p>Are you sure you want to delete this item?</p>
        <div className='w-full'><Button text="Confirm" danger={true} onClick={onConfirm(doctor.id, doctor.name)} />
        <Button text="Cancel" active onClick={onClose} /></div>
        
      </div>
    </div>
    </div>
  );
};

export default DeleteModal;
