import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useParams } from 'react-router-dom';
import Button from './Button';
import AddDoctorModal from './AddDoctorModal'; // Import the modal component for adding doctors

const Block = ({
  group,
  setGroup,
  fromDate,
  setFromDate,
  toDate,
  setToDate,
  filterData,
  setFilterData,
}) => {
  const [active, setActive] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchType, setSearchType] = useState('name');
  const [searchValue, setSearchValue] = useState('');

  const handleSearchTypeChange = (e) => {
    setSearchType(e.target.value);
  };

  const handleSearchValueChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleFilter = () => {
    let filteredResults = [];

    if (searchValue) {
      switch (searchType) {
        case 'name':
          filteredResults = group?.filter(
            (item) => {
              console.log(item)
              item.data.doctorDetails.name.toLowerCase().includes(searchValue.toLowerCase())}
          );
          break;
        case 'city':
          filteredResults = group?.filter(
            (item) => item.data.doctorDetails.city.toLowerCase().includes(searchValue.toLowerCase())
          );
          break;
        case 'hospital':
          filteredResults = group?.filter(
            (item) => item.data.doctorDetails.hospital.toLowerCase().includes(searchValue.toLowerCase())
          );
          break;
        default:
          break;
      }
    } else {
      filteredResults = group;
    }
    console.log(filteredResults)
    setFilterData(filteredResults);
  };

  useEffect(()=>{
    console.log(group);
  },[])

  return (
    <div className="bg-white shadow-md sm:rounded-lg flex justify-end p-8 flex-wrap gap-3">
      

      {/* Filter options */}
      <div className="bg-white shadow-md sm:rounded-lg flex justify-end p-8 flex-wrap gap-3">
      <div className="flex gap-2 items-center">
        <div className="w-6/12">
          <input
            type="text"
            placeholder="Search Value"
            value={searchValue}
            onChange={handleSearchValueChange}
            className="group--block--input rounded"
          />
        </div>
        <div className="w-6/12">
          <select
            value={searchType}
            onChange={handleSearchTypeChange}
            className="group--block--input rounded"
          >
            <option value="name">Name</option>
            <option value="city">City</option>
            <option value="hospital">Hospital</option>
          </select>
        </div>
        <div className="w-3/12">
          <button onClick={handleFilter} className="btn btn-primary">
            Filter
          </button>
        </div>
      </div>

      {/* Rest of the component content */}

      {errorMessage && (
        <div className="p-4 mb-4 text-sm text-red-800 rounded-lg bg-red-50 dark:bg-gray-800 dark:text-red-400 absolute top-2 left-50" role="alert">
          <span className="font-medium">Danger alert!</span> {errorMessage}
        </div>
      )}
    </div>
    <div className="w-2/12">
        <Button text="Add Doctor" active onClick={() => setIsModalOpen(true)} />
      </div>

      <AddDoctorModal
        isOpen={isModalOpen}
        closeModal={() => setIsModalOpen(false)}
        /* Add any other required props for the modal */
      />
    </div>
  );
};

export default Block;
