import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useParams } from 'react-router-dom';
import Button from './Button';
import AddDoctorModal from './AddDoctorModal'; // Import the modal component for adding doctors

const Block = ({
  group,
  setGroup,
  fromDate,
  setFromDate,
  toDate,
  setToDate,
  filterData,
  setFilterData,
}) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  // ... Other code ...

  return (
    <div className="bg-white shadow-md sm:rounded-lg flex justify-end p-8 flex-wrap gap-3">
      {/* ... Other code ... */}
      <div className="w-2/12">
        <Button text="Add Doctor" active onClick={() => setIsModalOpen(true)} />
      </div>

      {/* Filter options */}
      <div className="flex items-center space-x-4">
        <label className="flex items-center cursor-pointer">
          <div className="relative">
            <input type="checkbox" className="sr-only" />
            <div
              className={`block bg-gray-600 w-10 h-6 rounded-full transition ${
                /* Add conditional classes based on filter status */
                filterData.length > 0 ? 'bg-blue-500' : 'bg-gray-600'
              }`}
            ></div>
            <div
              className={`dot absolute top-1 w-4 h-4 rounded-full transition ${
                /* Add conditional classes based on filter status */
                filterData.length > 0 ? 'bg-blue-400 left-5' : 'bg-gray-400 left-1'
              }`}
            ></div>
          </div>
          <div className="ml-3 text-gray-700 font-medium">Filter Answered</div>
        </label>
      </div>

      <AddDoctorModal
        isOpen={isModalOpen}
        closeModal={() => setIsModalOpen(false)}
        /* Add any other required props for the modal */
      />
    </div>
  );
};

export default Block;
