import React, { useEffect, useState } from 'react';
import { fetchDoctorById, addDoctorToGroup} from '../../api/api';

const AddDoctorModal = ({ isModalOpen, setIsModalOpen, updateDoctor, currentGroup }) => {
  const [doctorDetails, setDoctorDetails] = useState({
    clinicAddress: '',
    phone: '',
    city: '',
    name: '',
    designation: '',
    hospital: '',
    email: '',
  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctorDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if(updateDoctor){
      
    }else{
      try{
        const response  = addDoctorToGroup(currentGroup, doctorDetails );
        console.log(response)
        setDoctorDetails({
          clinicAddress: '',
          phone: '',
          city: '',
          name: '',
          designation: '',
          hospital: '',
          email: '',
        });
      }catch(error){
        console.log(error)
      }
    }

    console.log('Doctor details:', doctorDetails);
    setIsModalOpen();// Close the modal after submitting
  };

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const response = await fetchDoctorById(updateDoctor, currentGroup);
          console.log(response.data)
          setDoctorDetails({
            clinicAddress: response.doctor.doctorDetails.clinicAddress,
            phone: response.doctor.doctorDetails.phone,
            city: response.doctor.doctorDetails.city,
            name: response.doctor.doctorDetails.name,
            designation: response.doctor.doctorDetails.designation,
            hospital: response.doctor.doctorDetails.hospital,
            email: response.doctor.doctorDetails.email,
          })
            // console.log(response.data.groupDetails)
          }catch(error) 
          {
          console.error('Error fetching group details:', error);
          };
    }


    if(isModalOpen){
      if(updateDoctor){
        fetchDoctor();
      }
    }
    
  },[isModalOpen])

  return (
    <div
      className={`fixed inset-0 flex items-center justify-center z-50 ${
        isModalOpen ? 'visible' : 'invisible'
      }`}
    >
      <div className="modal-container bg-white rounded p-20">
        <div className="modal-content">
          <h2 className="text-2xl mb-4">Add Doctor</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block mb-2" htmlFor="name">
                Name:
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={doctorDetails.name}
                onChange={handleChange}
                className="w-full border rounded p-2"
              />
            </div>
            <div className="mb-4">
              <label className="block mb-2" htmlFor="designation">
                Designation:
              </label>
              <input
                type="text"
                id="designation"
                name="designation"
                value={doctorDetails.designation}
                onChange={handleChange}
                className="w-full border rounded p-2"
              />
            </div>
            <div className="mb-4">
              <label className="block mb-2" htmlFor="hospital">
                Hospital:
              </label>
              <input
                type="text"
                id="hospital"
                name="hospital"
                value={doctorDetails.hospital}
                onChange={handleChange}
                className="w-full border rounded p-2"
              />
            </div>
            <div className="mb-4">
              <label className="block mb-2" htmlFor="clinicAddress">
                Clinic Address:
              </label>
              <input
                type="text"
                id="clinicAddress"
                name="clinicAddress"
                value={doctorDetails.clinicAddress}
                onChange={handleChange}
                className="w-full border rounded p-2"
              />
            </div>
            <div className="mb-4">
              <label className="block mb-2" htmlFor="phone">
                Phone:
              </label>
              <input
                type="text"
                id="phone"
                name="phone"
                value={doctorDetails.phone}
                onChange={handleChange}
                className="w-full border rounded p-2"
              />
            </div>
            <div className="mb-4">
              <label className="block mb-2" htmlFor="city">
                City:
              </label>
              <input
                type="text"
                id="city"
                name="city"
                value={doctorDetails.city}
                onChange={handleChange}
                className="w-full border rounded p-2"
              />
            </div>
            <div className="mb-4">
              <label className="block mb-2" htmlFor="email">
                Email:
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={doctorDetails.email}
                onChange={handleChange}
                className="w-full border rounded p-2"
              />
            </div>

            <div className="flex justify-end">
              <button
                type="submit"
                className="bg-blue-500 text-white px-4 py-2 rounded"
              >
                Save
              </button>
              <button
                type="button"
                className="ml-2 bg-gray-300 px-4 py-2 rounded"
                onClick={setIsModalOpen}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddDoctorModal;
