import React, { useState } from 'react';
import { createGroup } from '../../api/api';  // Import the API functions
import Button from './Button';

const Block = ({ isGroupAdded, setIsGroupAdded }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [groupName, setGroupName] = useState('');
  const [description, setDescription] = useState('')

  const handleAddGroup = () => {
    const data = {
      "groupName": groupName,
      "description": description
    }
    createGroup(data)
      .then((response) => {
        setIsGroupAdded(!isGroupAdded);
        setGroupName('');
        alert('Success: Group Created Successfully');
        closeModal();
      })
      .catch((error) => {
        console.error('Error creating group:', error);
        alert('Error: Unable to create the group');
      });
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="bg-white shadow-md sm:rounded-lg flex justify-end p-8">
      <div className="w-full flex gap-2 items-center">
        <div className="w-2/12">
          <Button icon="/imgs/plus.png" onClick={openModal} text="Add Group" active />
        </div>
      </div>

      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-10">
          <div className="modal bg-white p-6 rounded shadow-lg w-[500px] h-[300px]" style={{ margin: '0 auto' }}>
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <div className="modal-content">
              <h2>Enter Group Details</h2>
              <div className="group-details">
              <div className="mb-4">
                <label>Group Name:</label>
                <input
                  type="text"
                  value={groupName}
                  onChange={(e) => {
                    setGroupName(e.target.value);
                  }}
                />
                </div>
                <div className="mb-4">
        <label htmlFor="description">
          Description: 
        </label>
        <textarea
          className="appearance-none border-0 rounded"
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
              </div>
              <div className="modal-actions">
                <button onClick={handleAddGroup}>Save Group</button>
                <button onClick={closeModal}>Cancel</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};


export default Block;
