import React from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css'; // Import the styles

function EmailEditor({ subject, content, onSubjectChange, onContentChange }) {
  return (
    <div>
      <div class="relative">
    <input type="text" value={subject} onChange={(e) => onSubjectChange(e.target.value)} id="floating_outlined" class="block px-2.5 pb-2.5 pt-4 w-full text-sm text-gray-900 bg-transparent rounded-lg border-gray-300 appearance-none dark:text-white dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
    <label for="floating_outlined" class="absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-4 scale-75 top-2 z-10 origin-[0] bg-white dark:bg-gray-900 px-2 peer-focus:px-2 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:-translate-y-1/2 peer-placeholder-shown:top-1/2 peer-focus:top-2 peer-focus:scale-75 peer-focus:-translate-y-4 left-1">Subject</label>
</div>
      <label>Subject: </label>
      <input type="text"  />
      <br />
      <label>Email Content: </label>
      <ReactQuill
        value={content}
        onChange={onContentChange}
        modules={{ toolbar: true }} // You can customize the toolbar options
      />
    </div>
  );
}

export default EmailEditor;
