import React, { useEffect, useState } from 'react';
import Modal from './Modal';
import { useParams } from 'react-router-dom';
import { getDoctorsForGroup, deleteDoctor } from '../../api/api'; // Import the getDoctorsForGroup function

const TableDetail = ({ group, fromDate, toDate, filterData, setFilterData }) => {
  const [open, setOpen] = useState(false);
  const { id } = useParams();
  const [doctors, setDoctors] = useState([]);
  const [allGroups, setAllGroups] = useState([]);

  useEffect(() => {
    // Fetch doctor details for the group
    const fetchDoctors = async () => {
      try {
        const doctorData = await getDoctorsForGroup(id);
        setDoctors(doctorData);
        console.log(doctorData)
      } catch (error) {
        console.error('Error fetching doctor details:', error);
      }
    };

    allGroups?.filter((itm) => {
      if (selectedGroupId) {
        return itm?._id === selectedGroupId;
      } else {
        return true;
      }
    })

    fetchDoctors();
  }, [id]);

  const handleDeleteDoctor = async (doctorId) => {
    try {
      await deleteDoctor(doctorId);
      // Perform any additional actions after successfully deleting the doctor
      console.log(`Doctor with ID ${doctorId} has been deleted successfully.`);
    } catch (error) {
      console.error('Error deleting doctor:', error);
    }
  };

  return (
    <div>
    
      <div style={{ width: '100%' }} className="py-5 flex gap-5">
        <h1>Group Name:</h1>
        <p>{group?.name}</p>
      </div>
      <div className="relative overflow-x-auto shadow-md sm:rounded-lg mt-10">
        <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400 bg-white">
          {/* ... Table header and other code ... */}
          <tbody>
            {doctors.map((doctor, index) => (
              <tr key={index} className="bg-white border-b dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600">
                <td className="px-6 py-4 text-gray-900 whitespace-nowrap dark:text-white">
                  <div className="flex items-center">
                    <div className="w-10 h-10 rounded-full bg-gray-200">
                      {/* You may replace this with an image of the doctor */}
                    </div>
                    <div className="pl-3">
                      <div className="text-base font-semibold">{doctor.data.doctorDetails.name}</div>
                      <div>{doctor.data.doctorDetails.designation}</div>
                      <div>{doctor.data.doctorDetails.hospital}</div>
                      <div>{doctor.data.doctorDetails.city}</div>
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 text-center">{doctor.data.doctorDetails.phone}</td>
                <td className="px-6 py-4 text-center">{doctor.data.doctorDetails.email}</td>
                <td className="px-6 py-4 text-center">{doctor.data.doctorDetails.clinicAddress}</td>
                <td className="px-6 py-4 text-center">{/* Add any other doctor details here */}</td>
                <td className="px-6 py-4 text-center">
                  <button className="text-blue-500" >
                    Edit
                  </button>
                </td>
                <td className="px-6 py-4 text-center">
                  <button className="text-red-500" onClick={() => handleDeleteDoctor(doctor.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TableDetail;
