import React, { useState } from 'react';
import { createGroup } from '../../api/api';  // Import the API functions
import Button from './Button';

const Block = ({ isGroupAdded, setIsGroupAdded }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [groupName, setGroupName] = useState('');
  const [description, setDescription] = useState('')

  const handleAddGroup = () => {
    if (!groupName || !description) {
      setIsGroupAdded('Error: Group Name and Description cannot be empty');
      return;
    }
    const data = {
      "groupName": groupName,
      "description": description
    }
    createGroup(data)
      .then((response) => {
        setIsGroupAdded('Success: Group Created Successfully');
        setGroupName('');
        closeModal();
      })
      .catch((error) => {
        setIsGroupAdded('Error: Unable to create the group');
        console.error('Error creating group:', error);
      });
  };

  const openModal = () => {
    setIsModalOpen(true);
  };


  return (
    <div className="bg-white shadow-md sm:rounded-lg flex justify-end p-8">
      <div className="w-full flex gap-2 items-center">
        <div className="w-2/12">
          <Button icon="/imgs/plus.png" onClick={openModal} text="Add Group" active />
        </div>
      </div>

      
    </div>
  );
};


export default Block;
