    import React, { useRef, useState, useEffect } from "react";
    
import { createGroup, getGroupById } from '../../api/api';  // Import the API functions


    const Modal = ({ isGroupAdded, setIsGroupAdded, isModalOpen, setIsModalOpen, updateGroup}) => {

        const [groupName, setGroupName] = useState('');
        const [description, setDescription] = useState('')
        const [groupDetails, setGroupDetails] = useState(null);

        useEffect(() => {
            if (updateGroup) {
              // Fetch group details by its ID
              getGroupById(updateGroup)
                .then((response) => {
                  setGroupDetails(response.data.groupDetails); // Update group details state
                  console.log(response.data.groupDetails)
                })
                .catch((error) => {
                  console.error('Error fetching group details:', error);
                });
            }
          }, [updateGroup]);

      const handleAddGroup = () => {
        if (!groupName || !description) {
          setIsGroupAdded('Error: Group Name and Description cannot be empty');
          return;
        }
        const data = {
          "groupName": groupName,
          "description": description
        }
        createGroup(data)
          .then((response) => {
            setIsGroupAdded('Success: Group Created Successfully');
            setGroupName('');
            closeModal();
          })
          .catch((error) => {
            setIsGroupAdded('Error: Unable to create the group');
            console.error('Error creating group:', error);
          });
      };

      const closeModal = () => {
        setIsModalOpen(false);
      };
  

    return (
        <>
        {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-10">
          <div className="modal bg-white p-6 rounded shadow-lg w-[500px] h-[300px]" style={{ margin: '0 auto' }}>
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <div className="modal-content">
              <h2>Enter Group Details</h2>
              <div className="group-details">
              <div className="mb-4">
                <label>Group Name:</label>
                <input
                  type="text"
                  value={groupDetails? groupDetails.name :groupName}
                  onChange={(e) => {
                    setGroupName(e.target.value);
                  }}
                />
                </div>
                <div className="mb-4">
        <label htmlFor="description">
          Description: 
        </label>
        <textarea
          className="appearance-none border-0 rounded"
          id="description"
          value={groupDetails? groupDetails.description :description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
              </div>
              <div className="modal-actions">
                <button onClick={handleAddGroup}>Save Group</button>
                <button onClick={closeModal}>Cancel</button>
              </div>
            </div>
          </div>
        </div>
      )}
        </>
    );
    };

    export default Modal;
